    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Registro_paciente rp = new Registro_paciente();
        String btns[] = {"Si", "No"};
        
        int seguro = JOptionPane.showOptionDialog(rootPane, "Â¿El paciente a registrar a dado positivo en covid-19? ",
                           "", 0, 0, null, btns, null);
                   
                   if(seguro == JOptionPane.YES_OPTION){
                       rp.setVisible(true);
                       this.dispose();
                   }
                   
                   if(seguro == JOptionPane.NO_OPTION){
                       JOptionPane.showMessageDialog(rootPane, "Lo sentimos el registro solo es para pacientes que han dado positivo en covid-19");
                   }
               
        
    }//GEN-LAST:event_jButton1ActionPerformed

